const buyButton=document.querySelector(".navbtn");buyButton.addEventListener("click",function(){confirm("Confirm purchase?")});const socialNetworks=document.querySelectorAll(".socialnetworks");for(let e=0;e<socialNetworks.length;e++)socialNetworks[e].addEventListener("mouseover",function(e){e.preventDefault(),socialNetworks.forEach(e=>{e.classList.toggle("_blur")})}),socialNetworks[e].addEventListener("mouseout",function(e){e.preventDefault(),socialNetworks.forEach(e=>{e.classList.toggle("_blur")})});